	protected static function doOnDeleteCascade(Criteria $criteria, PropelPDO $con)
	{
		// initialize var to track total num of affected rows
		$affectedRows = 0;

		// first find the objects that are implicated by the $criteria
		$objects = CareerSubjectPeer::doSelect($criteria, $con);
		foreach ($objects as $obj) {


			// delete related Correlative objects
			$c = new Criteria(CorrelativePeer::DATABASE_NAME);
			
			$c->add(CorrelativePeer::CAREER_SUBJECT_ID, $obj->getId());
			$affectedRows += CorrelativePeer::doDelete($c, $con);

