				$this->collDivisionStudents = array();
			} else {

				$criteria->add(DivisionStudentPeer::DIVISION_ID, $this->id);

				$this->collDivisionStudents = DivisionStudentPeer::doSelectJoinStudent($criteria, $con, $join_behavior);
			}
		} else {
			// the following code is to determine if a new query is
			// called for.  If the criteria is the same as the last
			// one, just return the collection.

			$criteria->add(DivisionStudentPeer::DIVISION_ID, $this->id);

			if (!isset($this->lastDivisionStudentCriteria) || !$this->lastDivisionStudentCriteria->equals($criteria)) {
				$this->collDivisionStudents = DivisionStudentPeer::doSelectJoinStudent($criteria, $con, $join_behavior);
			}
		}
		$this->lastDivisionStudentCriteria = $criteria;

		return $this->collDivisionStudents;
	}

	/**
	 * Resets all collections of referencing foreign keys.
	 *
	 * This method is a user-space workaround for PHP's inability to garbage collect objects
	 * with circular references.  This is currently necessary when using Propel in certain
	 * daemon or large-volumne/high-memory operations.
	 *
	 * @param      boolean $deep Whether to also clear the references on all associated objects.
	 */
	public function clearAllReferences($deep = false)
	{
		if ($deep) {
			if ($this->collCoursesRelatedByDivisionId) {
				foreach ((array) $this->collCoursesRelatedByDivisionId as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collCoursesRelatedByRelatedDivisionId) {
				foreach ((array) $this->collCoursesRelatedByRelatedDivisionId as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collCourseSubjectConfigurations) {
				foreach ((array) $this->collCourseSubjectConfigurations as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collDivisionPreceptors) {
				foreach ((array) $this->collDivisionPreceptors as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collDivisionStudents) {
				foreach ((array) $this->collDivisionStudents as $o) {
					$o->clearAllReferences($deep);
				}
			}
		} // if ($deep)

		$this->collCoursesRelatedByDivisionId = null;
		$this->collCoursesRelatedByRelatedDivisionId = null;
		$this->collCourseSubjectConfigurations = null;
		$this->collDivisionPreceptors = null;
		$this->collDivisionStudents = null;
			$this->aDivisionTitle = null;
			$this->aCareerSchoolYear = null;
			$this->aShift = null;
	}


  public function __call($method, $arguments)
  {
    if (!$callable = sfMixer::getCallable('BaseDivision:'.$method))
    {
      throw new sfException(sprintf('Call to undefined method BaseDivision::%s', $method));
    }

    array_unshift($arguments, $this);

    return call_user_func_array($callable, $arguments);
  }


