			$criteria->add(StudentExaminationRepprovedSubjectPeer::EXAMINATION_REPPROVED_SUBJECT_ID, $this->id);

			if (!isset($this->lastStudentExaminationRepprovedSubjectCriteria) || !$this->lastStudentExaminationRepprovedSubjectCriteria->equals($criteria)) {
				$this->collStudentExaminationRepprovedSubjects = StudentExaminationRepprovedSubjectPeer::doSelectJoinStudentRepprovedCourseSubject($criteria, $con, $join_behavior);
			}
		}
		$this->lastStudentExaminationRepprovedSubjectCriteria = $criteria;

		return $this->collStudentExaminationRepprovedSubjects;
	}

	/**
	 * Resets all collections of referencing foreign keys.
	 *
	 * This method is a user-space workaround for PHP's inability to garbage collect objects
	 * with circular references.  This is currently necessary when using Propel in certain
	 * daemon or large-volumne/high-memory operations.
	 *
	 * @param      boolean $deep Whether to also clear the references on all associated objects.
	 */
	public function clearAllReferences($deep = false)
	{
		if ($deep) {
			if ($this->collExaminationRepprovedSubjectTeachers) {
				foreach ((array) $this->collExaminationRepprovedSubjectTeachers as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collStudentExaminationRepprovedSubjects) {
				foreach ((array) $this->collStudentExaminationRepprovedSubjects as $o) {
					$o->clearAllReferences($deep);
				}
			}
		} // if ($deep)

		$this->collExaminationRepprovedSubjectTeachers = null;
		$this->collStudentExaminationRepprovedSubjects = null;
			$this->aExaminationRepproved = null;
			$this->aCareerSubject = null;
	}


