	/**
	 * Method perform an INSERT on the database, given a Person or Criteria object.
	 *
	 * @param      mixed $values Criteria or Person object containing data that is used to create the INSERT statement.
	 * @param      PropelPDO $con the PropelPDO connection to use
	 * @return     mixed The new primary key.
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function doInsert($values, PropelPDO $con = null)
	{

    foreach (sfMixer::getCallables('BasePersonPeer:doInsert:pre') as $callable)
    {
      $ret = call_user_func($callable, 'BasePersonPeer', $values, $con);
      if (false !== $ret)
      {
        return $ret;
      }
    }


		if ($con === null) {
			$con = Propel::getConnection(PersonPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		if ($values instanceof Criteria) {
			$criteria = clone $values; // rename for clarity
		} else {
			$criteria = $values->buildCriteria(); // build Criteria from Person object
		}

		if ($criteria->containsKey(PersonPeer::ID) && $criteria->keyContainsValue(PersonPeer::ID) ) {
			throw new PropelException('Cannot insert a value for auto-increment primary key ('.PersonPeer::ID.')');
		}


		// Set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		try {
			// use transaction because $criteria could contain info
			// for more than one table (I guess, conceivably)
			$con->beginTransaction();
			$pk = BasePeer::doInsert($criteria, $con);
			$con->commit();
		} catch(PropelException $e) {
			$con->rollBack();
			throw $e;
		}

		
    foreach (sfMixer::getCallables('BasePersonPeer:doInsert:post') as $callable)
    {
      call_user_func($callable, 'BasePersonPeer', $values, $con, $pk);
    }

    return $pk;
	}

	/**
	 * Method perform an UPDATE on the database, given a Person or Criteria object.
	 *
	 * @param      mixed $values Criteria or Person object containing data that is used to create the UPDATE statement.
	 * @param      PropelPDO $con The connection to use (specify PropelPDO connection object to exert more control over transactions).
	 * @return     int The number of affected rows (if supported by underlying database driver).
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function doUpdate($values, PropelPDO $con = null)
	{

    foreach (sfMixer::getCallables('BasePersonPeer:doUpdate:pre') as $callable)
    {
      $ret = call_user_func($callable, 'BasePersonPeer', $values, $con);
      if (false !== $ret)
      {
        return $ret;
      }
    }


		if ($con === null) {
			$con = Propel::getConnection(PersonPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		$selectCriteria = new Criteria(self::DATABASE_NAME);

		if ($values instanceof Criteria) {
			$criteria = clone $values; // rename for clarity

			$comparison = $criteria->getComparison(PersonPeer::ID);
			$selectCriteria->add(PersonPeer::ID, $criteria->remove(PersonPeer::ID), $comparison);

		} else { // $values is Person object
			$criteria = $values->buildCriteria(); // gets full criteria
			$selectCriteria = $values->buildPkeyCriteria(); // gets criteria w/ primary key(s)
		}

		// set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		$ret = BasePeer::doUpdate($selectCriteria, $criteria, $con);
	

    foreach (sfMixer::getCallables('BasePersonPeer:doUpdate:post') as $callable)
    {
      call_user_func($callable, 'BasePersonPeer', $values, $con, $ret);
    }

    return $ret;
  }

	/**
	 * Method to DELETE all rows from the person table.
	 *
	 * @return     int The number of affected rows (if supported by underlying database driver).
	 */
	public static function doDeleteAll($con = null)
	{
		if ($con === null) {
			$con = Propel::getConnection(PersonPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}
		$affectedRows = 0; // initialize var to track total num of affected rows
		try {
			// use transaction because $criteria could contain info
			// for more than one table or we could emulating ON DELETE CASCADE, etc.
			$con->beginTransaction();
			$affectedRows += PersonPeer::doOnDeleteCascade(new Criteria(PersonPeer::DATABASE_NAME), $con);
			PersonPeer::doOnDeleteSetNull(new Criteria(PersonPeer::DATABASE_NAME), $con);
			$affectedRows += BasePeer::doDeleteAll(PersonPeer::TABLE_NAME, $con);
			$con->commit();
			return $affectedRows;
		} catch (PropelException $e) {
			$con->rollBack();
			throw $e;
		}
	}

	/**
	 * Method perform a DELETE on the database, given a Person or Criteria object OR a primary key value.
	 *
	 * @param      mixed $values Criteria or Person object or primary key or array of primary keys
	 *              which is used to create the DELETE statement
	 * @param      PropelPDO $con the connection to use
	 * @return     int 	The number of affected rows (if supported by underlying database driver).  This includes CASCADE-related rows
	 *				if supported by native driver or if emulated using Propel.
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	 public static function doDelete($values, PropelPDO $con = null)
	 {
		if ($con === null) {
			$con = Propel::getConnection(PersonPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		if ($values instanceof Criteria) {
			// invalidate the cache for all objects of this type, since we have no
			// way of knowing (without running a query) what objects should be invalidated
			// from the cache based on this Criteria.
			PersonPeer::clearInstancePool();

			// rename for clarity
			$criteria = clone $values;
		} elseif ($values instanceof Person) {
			// invalidate the cache for this single object
			PersonPeer::removeInstanceFromPool($values);
			// create criteria based on pk values
			$criteria = $values->buildPkeyCriteria();
		} else {
			// it must be the primary key



			$criteria = new Criteria(self::DATABASE_NAME);
			$criteria->add(PersonPeer::ID, (array) $values, Criteria::IN);

			foreach ((array) $values as $singleval) {
				// we can invalidate the cache for this single object
				PersonPeer::removeInstanceFromPool($singleval);
			}
		}

		// Set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		$affectedRows = 0; // initialize var to track total num of affected rows

		try {
			// use transaction because $criteria could contain info
			// for more than one table or we could emulating ON DELETE CASCADE, etc.
			$con->beginTransaction();
			$affectedRows += PersonPeer::doOnDeleteCascade($criteria, $con);
			PersonPeer::doOnDeleteSetNull($criteria, $con);
			
				// Because this db requires some delete cascade/set null emulation, we have to
				// clear the cached instance *after* the emulation has happened (since
				// instances get re-added by the select statement contained therein).
				if ($values instanceof Criteria) {
					PersonPeer::clearInstancePool();
				} else { // it's a PK or object
					PersonPeer::removeInstanceFromPool($values);
				}
			
			$affectedRows += BasePeer::doDelete($criteria, $con);

			// invalidate objects in StudentDisciplinarySanctionPeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			StudentDisciplinarySanctionPeer::clearInstancePool();

			// invalidate objects in PersonalPeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			PersonalPeer::clearInstancePool();

			// invalidate objects in StudentPeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			StudentPeer::clearInstancePool();

			// invalidate objects in TeacherPeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			TeacherPeer::clearInstancePool();

			// invalidate objects in TutorPeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			TutorPeer::clearInstancePool();

			// invalidate objects in LicensePeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			LicensePeer::clearInstancePool();

			$con->commit();
			return $affectedRows;
		} catch (PropelException $e) {
			$con->rollBack();
			throw $e;
		}
	}

	/**
	 * This is a method for emulating ON DELETE CASCADE for DBs that don't support this
	 * feature (like MySQL or SQLite).
	 *
	 * This method is not very speedy because it must perform a query first to get
	 * the implicated records and then perform the deletes by calling those Peer classes.
	 *
	 * This method should be used within a transaction if possible.
	 *
	 * @param      Criteria $criteria
	 * @param      PropelPDO $con
	 * @return     int The number of affected rows (if supported by underlying database driver).
	 */
	protected static function doOnDeleteCascade(Criteria $criteria, PropelPDO $con)
	{
		// initialize var to track total num of affected rows
		$affectedRows = 0;

		// first find the objects that are implicated by the $criteria
		$objects = PersonPeer::doSelect($criteria, $con);
		foreach ($objects as $obj) {


			// delete related Personal objects
			$c = new Criteria(PersonalPeer::DATABASE_NAME);
			
			$c->add(PersonalPeer::PERSON_ID, $obj->getId());
			$affectedRows += PersonalPeer::doDelete($c, $con);

			// delete related Student objects
			$c = new Criteria(StudentPeer::DATABASE_NAME);
			
			$c->add(StudentPeer::PERSON_ID, $obj->getId());
			$affectedRows += StudentPeer::doDelete($c, $con);

			// delete related Teacher objects
			$c = new Criteria(TeacherPeer::DATABASE_NAME);
			
			$c->add(TeacherPeer::PERSON_ID, $obj->getId());
			$affectedRows += TeacherPeer::doDelete($c, $con);

			// delete related Tutor objects
			$c = new Criteria(TutorPeer::DATABASE_NAME);
			
			$c->add(TutorPeer::PERSON_ID, $obj->getId());
			$affectedRows += TutorPeer::doDelete($c, $con);

			// delete related License objects
			$c = new Criteria(LicensePeer::DATABASE_NAME);
			
			$c->add(LicensePeer::PERSON_ID, $obj->getId());
			$affectedRows += LicensePeer::doDelete($c, $con);
		}
		return $affectedRows;
	}

	/**
	 * This is a method for emulating ON DELETE SET NULL DBs that don't support this
	 * feature (like MySQL or SQLite).
	 *
	 * This method is not very speedy because it must perform a query first to get
	 * the implicated records and then perform the deletes by calling those Peer classes.
	 *
	 * This method should be used within a transaction if possible.
	 *
	 * @param      Criteria $criteria
	 * @param      PropelPDO $con
	 * @return     void
	 */
	protected static function doOnDeleteSetNull(Criteria $criteria, PropelPDO $con)
	{

		// first find the objects that are implicated by the $criteria
		$objects = PersonPeer::doSelect($criteria, $con);
		foreach ($objects as $obj) {

			// set fkey col in related StudentDisciplinarySanction rows to NULL
			$selectCriteria = new Criteria(PersonPeer::DATABASE_NAME);
			$updateValues = new Criteria(PersonPeer::DATABASE_NAME);
			$selectCriteria->add(StudentDisciplinarySanctionPeer::RESPONSIBLE_ID, $obj->getId());
			$updateValues->add(StudentDisciplinarySanctionPeer::RESPONSIBLE_ID, null);

					BasePeer::doUpdate($selectCriteria, $updateValues, $con); // use BasePeer because generated Peer doUpdate() methods only update using pkey

		}
	}

	/**
	 * Validates all modified columns of given Person object.
	 * If parameter $columns is either a single column name or an array of column names
	 * than only those columns are validated.
	 *
	 * NOTICE: This does not apply to primary or foreign keys for now.
	 *
	 * @param      Person $obj The object to validate.
	 * @param      mixed $cols Column name or array of column names.
	 *
	 * @return     mixed TRUE if all columns are valid or the error message of the first invalid column.
	 */
	public static function doValidate(Person $obj, $cols = null)
	{
		$columns = array();

		if ($cols) {
			$dbMap = Propel::getDatabaseMap(PersonPeer::DATABASE_NAME);
			$tableMap = $dbMap->getTable(PersonPeer::TABLE_NAME);

			if (! is_array($cols)) {
				$cols = array($cols);
			}

			foreach ($cols as $colName) {
				if ($tableMap->containsColumn($colName)) {
					$get = 'get' . $tableMap->getColumn($colName)->getPhpName();
					$columns[$colName] = $obj->$get();
				}
			}
		} else {

		}

		$res =  BasePeer::doValidate(PersonPeer::DATABASE_NAME, PersonPeer::TABLE_NAME, $columns);
    if ($res !== true) {
        foreach ($res as $failed) {
            $col = PersonPeer::translateFieldname($failed->getColumn(), BasePeer::TYPE_COLNAME, BasePeer::TYPE_PHPNAME);
        }
    }

    return $res;
	}

	/**
	 * Retrieve a single object by pkey.
	 *
	 * @param      int $pk the primary key.
	 * @param      PropelPDO $con the connection to use
	 * @return     Person
	 */
	public static function retrieveByPK($pk, PropelPDO $con = null)
	{

		if (null !== ($obj = PersonPeer::getInstanceFromPool((string) $pk))) {
			return $obj;
		}

		if ($con === null) {
			$con = Propel::getConnection(PersonPeer::DATABASE_NAME, Propel::CONNECTION_READ);
		}

		$criteria = new Criteria(PersonPeer::DATABASE_NAME);
		$criteria->add(PersonPeer::ID, $pk);

		$v = PersonPeer::doSelect($criteria, $con);

		return !empty($v) > 0 ? $v[0] : null;
	}

	/**
	 * Retrieve multiple objects by pkey.
	 *
	 * @param      array $pks List of primary keys
	 * @param      PropelPDO $con the connection to use
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function retrieveByPKs($pks, PropelPDO $con = null)
	{
		if ($con === null) {
			$con = Propel::getConnection(PersonPeer::DATABASE_NAME, Propel::CONNECTION_READ);
		}

		$objs = null;
		if (empty($pks)) {
			$objs = array();
		} else {
			$criteria = new Criteria(PersonPeer::DATABASE_NAME);
			$criteria->add(PersonPeer::ID, $pks, Criteria::IN);
			$objs = PersonPeer::doSelect($criteria, $con);
		}
		return $objs;
	}

