	/**
	 * Method perform a DELETE on the database, given a Student or Criteria object OR a primary key value.
	 *
	 * @param      mixed $values Criteria or Student object or primary key or array of primary keys
	 *              which is used to create the DELETE statement
	 * @param      PropelPDO $con the connection to use
	 * @return     int 	The number of affected rows (if supported by underlying database driver).  This includes CASCADE-related rows
	 *				if supported by native driver or if emulated using Propel.
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	 public static function doDelete($values, PropelPDO $con = null)
	 {
		if ($con === null) {
			$con = Propel::getConnection(StudentPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		if ($values instanceof Criteria) {
			// invalidate the cache for all objects of this type, since we have no
			// way of knowing (without running a query) what objects should be invalidated
			// from the cache based on this Criteria.
			StudentPeer::clearInstancePool();

			// rename for clarity
			$criteria = clone $values;
		} elseif ($values instanceof Student) {
			// invalidate the cache for this single object
			StudentPeer::removeInstanceFromPool($values);
			// create criteria based on pk values
			$criteria = $values->buildPkeyCriteria();
		} else {
			// it must be the primary key



			$criteria = new Criteria(self::DATABASE_NAME);
			$criteria->add(StudentPeer::ID, (array) $values, Criteria::IN);

			foreach ((array) $values as $singleval) {
				// we can invalidate the cache for this single object
				StudentPeer::removeInstanceFromPool($singleval);
			}
		}

		// Set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		$affectedRows = 0; // initialize var to track total num of affected rows

		try {
			// use transaction because $criteria could contain info
			// for more than one table or we could emulating ON DELETE CASCADE, etc.
			$con->beginTransaction();
			$affectedRows += StudentPeer::doOnDeleteCascade($criteria, $con);
			
				// Because this db requires some delete cascade/set null emulation, we have to
				// clear the cached instance *after* the emulation has happened (since
				// instances get re-added by the select statement contained therein).
				if ($values instanceof Criteria) {
					StudentPeer::clearInstancePool();
				} else { // it's a PK or object
					StudentPeer::removeInstanceFromPool($values);
				}
			
			$affectedRows += BasePeer::doDelete($criteria, $con);

			// invalidate objects in StudentCareerSubjectAllowedPeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			StudentCareerSubjectAllowedPeer::clearInstancePool();

			// invalidate objects in StudentStudyPeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			StudentStudyPeer::clearInstancePool();

			// invalidate objects in CareerStudentPeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			CareerStudentPeer::clearInstancePool();

			// invalidate objects in StudentCareerSchoolYearPeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			StudentCareerSchoolYearPeer::clearInstancePool();

			// invalidate objects in BrotherhoodPeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			BrotherhoodPeer::clearInstancePool();

			// invalidate objects in BrotherhoodPeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			BrotherhoodPeer::clearInstancePool();

			// invalidate objects in StudentTagPeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			StudentTagPeer::clearInstancePool();

			// invalidate objects in StudentTutorPeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			StudentTutorPeer::clearInstancePool();

			$con->commit();
			return $affectedRows;
		} catch (PropelException $e) {
			$con->rollBack();
			throw $e;
		}
	}

	/**
	 * This is a method for emulating ON DELETE CASCADE for DBs that don't support this
	 * feature (like MySQL or SQLite).
	 *
	 * This method is not very speedy because it must perform a query first to get
	 * the implicated records and then perform the deletes by calling those Peer classes.
	 *
	 * This method should be used within a transaction if possible.
	 *
	 * @param      Criteria $criteria
	 * @param      PropelPDO $con
	 * @return     int The number of affected rows (if supported by underlying database driver).
	 */
	protected static function doOnDeleteCascade(Criteria $criteria, PropelPDO $con)
	{
		// initialize var to track total num of affected rows
		$affectedRows = 0;

		// first find the objects that are implicated by the $criteria
		$objects = StudentPeer::doSelect($criteria, $con);
		foreach ($objects as $obj) {


			// delete related StudentCareerSubjectAllowed objects
			$c = new Criteria(StudentCareerSubjectAllowedPeer::DATABASE_NAME);
			
			$c->add(StudentCareerSubjectAllowedPeer::STUDENT_ID, $obj->getId());
			$affectedRows += StudentCareerSubjectAllowedPeer::doDelete($c, $con);

			// delete related StudentStudy objects
			$c = new Criteria(StudentStudyPeer::DATABASE_NAME);
			
			$c->add(StudentStudyPeer::STUDENT_ID, $obj->getId());
			$affectedRows += StudentStudyPeer::doDelete($c, $con);

			// delete related CareerStudent objects
			$c = new Criteria(CareerStudentPeer::DATABASE_NAME);
			
			$c->add(CareerStudentPeer::STUDENT_ID, $obj->getId());
			$affectedRows += CareerStudentPeer::doDelete($c, $con);

			// delete related StudentCareerSchoolYear objects
			$c = new Criteria(StudentCareerSchoolYearPeer::DATABASE_NAME);
			
			$c->add(StudentCareerSchoolYearPeer::STUDENT_ID, $obj->getId());
			$affectedRows += StudentCareerSchoolYearPeer::doDelete($c, $con);

			// delete related Brotherhood objects
			$c = new Criteria(BrotherhoodPeer::DATABASE_NAME);
			
			$c->add(BrotherhoodPeer::STUDENT_ID, $obj->getId());
			$affectedRows += BrotherhoodPeer::doDelete($c, $con);

			// delete related Brotherhood objects
			$c = new Criteria(BrotherhoodPeer::DATABASE_NAME);
			
			$c->add(BrotherhoodPeer::BROTHER_ID, $obj->getId());
			$affectedRows += BrotherhoodPeer::doDelete($c, $con);

			// delete related StudentTag objects
			$c = new Criteria(StudentTagPeer::DATABASE_NAME);
			
			$c->add(StudentTagPeer::STUDENT_ID, $obj->getId());
			$affectedRows += StudentTagPeer::doDelete($c, $con);

			// delete related StudentTutor objects
			$c = new Criteria(StudentTutorPeer::DATABASE_NAME);
			
			$c->add(StudentTutorPeer::STUDENT_ID, $obj->getId());
			$affectedRows += StudentTutorPeer::doDelete($c, $con);
		}
		return $affectedRows;
	}

