					$o->clearAllReferences($deep);
				}
			}
			if ($this->collCareerSubjectSchoolYears) {
				foreach ((array) $this->collCareerSubjectSchoolYears as $o) {
					$o->clearAllReferences($deep);
				}
			}
		} // if ($deep)

		$this->collCareerYearConfigurations = null;
		$this->collCareerSchoolYears = null;
		$this->collCareerSubjectSchoolYears = null;
	}


  public function __call($method, $arguments)
  {
    if (!$callable = sfMixer::getCallable('BaseSubjectConfiguration:'.$method))
    {
      throw new sfException(sprintf('Call to undefined method BaseSubjectConfiguration::%s', $method));
    }

    array_unshift($arguments, $this);

    return call_user_func_array($callable, $arguments);
  }


